import React, { useState } from "react";
import { Avatar, Button, Card, Col, Row, Collapse, Drawer, Tag } from "antd";
import {
  ArrowRightOutlined,
  CheckCircleOutlined,
  PlayCircleOutlined,
} from "@ant-design/icons";
import "./ProductView.css";
import { CourseContent, courseProjects, termsData } from "./const";
import RegisterForm from "../../Utils/Register/Register";
import { StampCard } from "../../Utils/StampCard/StampCard";
import { DownArrowSvg } from "../../Assets/DownArrowSvg";
import { VideoPlaySvg } from "../../Assets/VideoPlaySvg";
import { LinuxSvg } from "../../Assets/CoursesSVG/LinuxSvg";
import { AnsibleSvg } from "../../Assets/CoursesSVG/AnsibleSvg";
import { AwsSvg } from "../../Assets/CoursesSVG/AwsSvg";
import { DockerSvg } from "../../Assets/CoursesSVG/DockerSvg";
import { GithubSvg } from "../../Assets/CoursesSVG/GithubSvg";
import { KubernetesSvg } from "../../Assets/CoursesSVG/KubernetesSvg";
import { MavenSvg } from "../../Assets/CoursesSVG/MavenSvg";
import { PythonSvg } from "../../Assets/CoursesSVG/PythonSvg";
import { UspCard } from "../../Utils/UspCard/UspCard";
import PaymentWidget from "sam-lms-payment-component";

const ProductView = () => {
  const [open, setOpen] = useState(false);

  const toolsSize = {
    height: "50px",
    width: "50px",
  };
  const stampData = [
    {
      title: "Certificate of completion",
      type: "certification",
    },
    {
      title: "Course duration 70hrs 15min",
      type: "duration",
    },
    {
      title: "Lessions 10",
      type: "lessions",
    },
    {
      title: "Level up your skills",
      type: "levelup",
    },
  ];

  const spaceBetween = (text, text2) => {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        <div>
          <Col>
            <div style={{ color: "#4e34ed" }}>{text}</div>
            <div style={{ color: "GrayText", fontSize: "12px" }}>3:23 sec</div>
          </Col>
        </div>
        <div>
          <div>
            <VideoPlaySvg />
            <a className="course-type">{text2}</a>
          </div>
        </div>
      </div>
    );
  };

  const items = (data, idx) => {
    return {
      key: idx,
      label: (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div className="label"> {data?.title}</div>{" "}
        </div>
      ),
      children: [
        <div>
          <ul className="custom-list">
            {data?.content.map((item) => (
              <li>{spaceBetween(item?.title, "play")}</li>
            ))}
          </ul>
        </div>,
      ],
    };
  };

  const handleChange = () => {};
  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        position: "fixed",
        display: "flex",
        background:
          "linear-gradient(357deg, rgba(0, 0, 0, 1) 0%, rgba(149, 151, 255, 1) 56%)",
      }}
    >
      {/* <div
        className="course-navigation"
        style={{
          width: "23%",
          height: "100%",
          overflowY: "auto",
        }}
      >
        <span
          className="title"
          style={{ paddingInline: "10px", fontSize: "30px" }}
        >
          Course Content
        </span>
        <Collapse
          expandIcon={({ isActive }) => <DownArrowSvg />}
          expandIconPosition="end"
          defaultActiveKey={[0]}
          onChange={handleChange}
          items={CourseContent.map((data, idx) => items(data, idx))}
          className="collapse"
        />
      </div> */}

      <div
        className="course-content"
        style={{
          width: "100%",
          height: "100%",
          marginTop: "20px",
          boxShadow: "grey 0px 1px 2px 0px, grey 0px 2px 6px 2px",
          overflowY: "auto",
        }}
      >
        <div
          style={{
            paddingLeft: "10px",
            backgroundColor: "white",
            display: "flex",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              rowGap: "20px",
              padding: "10px",
            }}
          >
            <Card
              title={
                <div
                  style={{
                    justifyContent: "space-between",
                  }}
                >
                  <div>
                    <Row>
                      <Avatar
                        src="https://i.pinimg.com/564x/b9/90/b9/b990b9b5ea80ef423092bbe9bfd822ef.jpg"
                        size={100}
                        style={{
                          marginRight: "10%",
                        }}
                      />
                      <Col>
                        <Col style={{ fontSize: "15px" }}>
                          Devops training certification
                        </Col>
                        <Col style={{ color: "#989a9c" }}>
                          Master DevOps Engineer Course Online
                        </Col>
                      </Col>
                    </Row>
                  </div>
                </div>
              }
              style={{
                width: "94%",
                boxShadow:
                  "rgba(17, 17, 26, 0.1) 0px 8px 24px, rgba(17, 17, 26, 0.1) 0px 16px 56px, rgba(17, 17, 26, 0.1) 0px 24px 80px",
                height: "20%",
                display: "flex",
              }}
            >
              <img
                alt="example"
                src="https://i.pinimg.com/736x/b6/ca/9a/b6ca9aa53cf9b7d19d28c28f9f2fca06.jpg"
                style={{ height: "250px", width: "350px" }}
              />
              <Row className="clipArt">
                <svg
                  className="sine-wave-background"
                  viewBox="10 0 100 20"
                  xmlns="http://www.w3.org/2000/svg"
                  style={{ position: "absolute" }}
                >
                  <circle
                    className="circle"
                    cx="24"
                    cy="24"
                    r="12"
                    fill="#c2cbc95b"
                  />
                </svg>
              </Row>
            </Card>
            <div style={{ fontSize: "22px" }}>Course Includes</div>
            <div
              style={{
                display: "flex",
                justifyContent: "space-around",
                height: "11%",
              }}
            >
              {stampData.map((data) => (
                <StampCard data={data} />
              ))}
            </div>
            <div style={{ marginTop: "10%" }}>
              <div style={{ fontSize: "22px" }}>Tools Covered</div>
              <div
                style={{
                  width: "100%",
                  display: "flex",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Col>
                  <LinuxSvg data={toolsSize} />
                </Col>
                <Col>
                  <PythonSvg data={toolsSize} />
                </Col>
                <Col>
                  <AwsSvg data={toolsSize} />
                </Col>
                <Col>
                  <DockerSvg data={toolsSize} />
                </Col>
                <Col>
                  <AnsibleSvg data={toolsSize} />
                </Col>
                <Col>
                  <GithubSvg data={toolsSize} />
                </Col>
                <Col>
                  <KubernetesSvg data={toolsSize} />
                </Col>
                <Col>
                  <MavenSvg
                    data={{
                      height: "80px",
                      width: "100px",
                    }}
                  />
                </Col>
              </div>
            </div>
            <div>
              <div style={{ fontSize: "22px" }}>Career Outcome</div>
              <div
                style={{
                  fontSize: "14px",
                  color: "GrayText",
                  textWrap: "pretty",
                  textAlign: "justify",
                }}
              >
                System Reliability Engineer (SRE), Platform Engineer, Cloud
                Engineer/Architect
              </div>
            </div>
            <div>
              <div style={{ fontSize: "22px" }}>Requirements</div>
              <div style={{ fontSize: "14px", color: "GrayText" }}>
                Basic Computer Knowledge
              </div>
            </div>
            <div>
              <div style={{ fontSize: "22px" }}>Capstone Projects</div>
              <div
                style={{
                  width: "100%",
                  display: "flex",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                {courseProjects.map((usp, index) => (
                  <Col span={8} key={index}>
                    <UspCard data={[usp, index]} />
                  </Col>
                ))}
              </div>
            </div>

            <div>
              <div style={{ fontSize: "22px" }}>Description</div>
              <div
                style={{
                  fontSize: "14px",
                  color: "GrayText",
                  textWrap: "pretty",
                  textAlign: "justify",
                }}
              >
                This course is for anybody who wants to get started with DevOps:
                As there are many tools & technologies in DevOps, it has become
                difficult for people to choose the right technologies. This
                course starts from very basics of command line, hands on
                demonstrations of many tools & technologies. Also most
                importantly it will show you how various technologies in DevOps
                work together by setting up your own projects.
              </div>
            </div>
            <div>
              <div style={{ fontSize: "22px" }}>Learning Insight</div>
              <div
                style={{
                  fontSize: "14px",
                  color: "GrayText",
                  textWrap: "pretty",
                  textAlign: "justify",
                }}
              >
                Python Fundamentals, Data Structures and Algorithms, Linux,
                DevOps Tools, AWS, System Design, Advanced DSA
              </div>
            </div>
            <div>
              <div style={{ fontSize: "22px" }}>What You'll Learn</div>
              <div
                style={{
                  display: "flex",
                  width: "100%",
                  flexWrap: "wrap",
                  height: "180px",
                }}
              >
                {termsData.map((row) => (
                  <div style={{ width: "220px" }}>
                    <Row
                      style={{
                        display: "flex",
                        justifyContent: "start",
                        alignItems: "start",
                        width: "220px",
                        fontSize: "12px",
                      }}
                    >
                      <Row style={{ width: "20px", marginTop: "3px" }}>
                        <CheckCircleOutlined style={{ color: "green" }} />
                      </Row>
                      <Row style={{ width: "200px" }}>
                        <span style={{ color: "GrayText" }}>{row}</span>
                      </Row>
                    </Row>
                  </div>
                ))}
              </div>
            </div>
            <div>&nsbp;</div>
          </div>
          <div
            style={{
              width: "100%",
              marginTop: "10px",
              paddingInline: "10px",
              display: "flex",
              flexDirection: "column",
            }}
          >
            <Card className="payment-card" style={{ borderRadius: "10px" }}>
              <Col style={{ position: "relative", zIndex: 1 }}>
                <Row
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <span style={{ fontSize: "22px", fontWeight: "300" }}>
                    Enroll Now
                  </span>
                  <Tag
                    className="heighlight"
                    color="green"
                    bordered={false}
                    style={{
                      fontSize: "14px",
                      fontWeight: "300",
                      padding: "3px",
                    }}
                  >
                    20% discount
                  </Tag>
                </Row>
                <div>
                  <div style={{ color: "grey" }}>
                    Climb higher education that{" "}
                    <span style={{ color: "black" }}>elevates</span>
                  </div>
                  <div style={{ alignItems: "center", marginTop: "4px" }}>
                    <div style={{ color: "gray" }}>Price</div>
                    <span
                      style={{ fontSize: "22px", color: "", fontWeight: "200" }}
                    >
                      1600
                    </span>
                    <Tag
                      color="gold"
                      bordered={false}
                      style={{
                        marginLeft: "10px",
                        textDecoration: "line-through",
                      }}
                    >
                      2000
                    </Tag>
                  </div>
                </div>
                <center>
                  <Button
                    className="enroll-button"
                    onClick={() => setOpen(true)}
                  >
                    Enroll
                    <ArrowRightOutlined className="enroll-icon" />
                  </Button>
                </center>
              </Col>
              <div style={{ zIndex: 1 }}>
                <svg
                  width="200"
                  height="300"
                  viewBox="0 0 200 300"
                  style={{
                    position: "absolute",
                    top: "0%",
                    right: "0%",
                    left: "0%",
                    opacity: "0.1",
                  }}
                >
                  <circle
                    class="circle one"
                    cx="100"
                    cy="10"
                    r="70"
                    fill="gray"
                  />
                </svg>
              </div>
              <div style={{ zIndex: 0 }}>
                <svg
                  width="200"
                  height="300"
                  viewBox="0 0 200 300"
                  style={{
                    position: "absolute",
                    top: "0%",
                    left: "0%",
                    opacity: "0.11",
                  }}
                >
                  <circle
                    class="circle one"
                    cx="100"
                    cy="10"
                    r="100"
                    fill="gray"
                  />
                </svg>
              </div>
            </Card>
            <Card>
              <PaymentWidget apiUrl="https://2f4d-2406-b400-d5-4f90-70-9f52-2f38-9b97.ngrok-free.app/api" />
            </Card>
            <Drawer
              placement={"right"}
              closable={false}
              onClose={() => setOpen(false)}
              open={open}
              style={{
                position: "absolute",
                height: "100%",
                borderRadius: "25px",
              }}
            >
              <RegisterForm />
            </Drawer>
          </div>
        </div>
      </div>
    </div>
  );
};
export default ProductView;
