import React, { useState } from "react";
import { ReactComponent as GoogleSvg } from "../../Assets/google-color-svg.svg";
import {
  Form,
  Input,
  Button,
  Checkbox,
  Typography,
  Row,
  Col,
  Divider,
} from "antd";
import { GoogleOutlined, LinkedinOutlined } from "@ant-design/icons";
import { TextWithHrLine } from "../../Utils/TextWithHrLine/TextWithHrLine";

const { Title, Text } = Typography;

const LoginForm = () => {
  const [passwordVisible, setPasswordVisible] = useState(false);

  const handleLogin = (values) => {
    console.log("Form values:", values);
  };

  const GoogleIcon = () => (
    <svg
      width="24"
      height="24"
      viewBox="0 0 24 24"
      xmlns="http://www.w3.org/2000/svg"
    >
      {/* Paste SVG content here */}
    </svg>
  );

  return (
    <>
      <Row justify="center">
        <span
          style={{
            textAlign: "center",
            fontSize: "20px",
            fontWeight: "lighter",
          }}
        >
          SAM LMS
        </span>
      </Row>
      <Row
        justify="center"
        style={{
          margin: "16px 0",
          color: "gray",
          fontSize: "14px",
          fontWeight: "300",
        }}
      >
        <span>
          Login to your account. Welcome back! Select a method to login.
        </span>
      </Row>
      <Row gutter={16} justify="center" style={{ margin: "16px 0" }}>
        <Col>
          <Button icon={<GoogleIcon />} style={{ width: "100%" }}>
            Google
          </Button>
        </Col>
        <Col>
          <Button icon={<LinkedinOutlined />} style={{ width: "100%" }}>
            LinkedIn
          </Button>
        </Col>
      </Row>
      <Row
        justify="center"
        style={{ margin: "10px 0", fontSize: "12px", color: "grey" }}
      >
        <span>Or Continue With Email or Mobile</span>
      </Row>
      <Form
        name="login"
        initialValues={{ remember: true }}
        onFinish={handleLogin}
      >
        <Form.Item
          name="email"
          rules={[
            { required: true, message: "Please input your email/phone!" },
          ]}
        >
          <Input placeholder="Email or Phone" />
        </Form.Item>

        <Form.Item
          name="password"
          rules={[{ required: true, message: "Please input your password!" }]}
        >
          <Input.Password
            placeholder="Password"
            visibilityToggle={{
              visible: passwordVisible,
              onClick: () => setPasswordVisible(!passwordVisible),
            }}
          />
        </Form.Item>

        <Row gutter={16} align="middle">
          <Col span={12}>
            <Form.Item name="remember" valuePropName="checked">
              <Checkbox>Remember me</Checkbox>
            </Form.Item>
          </Col>
          <Col span={12} style={{ textAlign: "right" }}>
            <a href="/forgot-password">Forgot password?</a>
          </Col>
        </Row>

        <Form.Item>
          <Button type="primary" htmlType="submit" style={{ width: "100%" }}>
            Log in
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default LoginForm;
