import React, { useState } from "react";
import { Avatar, Button, Card, Col, Menu, Row, Collapse, Drawer } from "antd";
import {
  CheckCircleOutlined,
  ShareAltOutlined,
  PlayCircleOutlined,
  PlayCircleFilled,
  FundViewOutlined,
  EyeOutlined,
  ClockCircleFilled,
  LeftOutlined,
} from "@ant-design/icons";
import "./ProductView.css";
import { CourseContent, termsData } from "./const";
import RegisterForm from "../../Utils/Register/Register";
import { StapCard } from "../../Utils/SideBar/StapCard";
import { CertificationSvg } from "../../Assets/CretificationSvg";

const ProductView = () => {
  const [open, setOpen] = useState(false);

  const style = {
    border: "0.1px solid grey",
    borderBottom: "4px solid grey",
    background: "white",
    borderRadius: "10%",
  };
  console.log("SVG", CertificationSvg(style));

  const stapData = [
    {
      title: "Certificate of completion",
      svg: CertificationSvg(style),
    },
  ];

  const spaceBetween = (text, text2) => {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        <div>
          <Col>
            <div style={{ color: "#4e34ed" }}>{text}</div>
            <div style={{ color: "GrayText", fontSize: "12px" }}>3:23 sec</div>
          </Col>
        </div>
        <div>
          <div>
            <PlayCircleOutlined style={{ color: "#8576e3" }} />
            <a className="course-type" style={{ textDecoration: "underline" }}>
              {" "}
              {text2}
            </a>{" "}
          </div>
        </div>
      </div>
    );
  };

  const items = (data, idx) => {
    console.log(`idx ==> ${idx}`, data);
    return {
      key: idx,
      label: (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div className="label"> {data?.title}</div>{" "}
          <div style={{ color: "navy", width: "46px", fontSize: "13px" }}>
            {data?.duration}
          </div>
        </div>
      ),
      children: [
        <div>
          <ul className="custom-list">
            {data?.content.map((item) => (
              <li>{spaceBetween(item?.title, "play")}</li>
            ))}
          </ul>
        </div>,
      ],
    };
  };

  const handleChange = () => {};
  return (
    <div
      style={{
        height: "100%",
      }}
    >
      {/* <Header /> */}
      <div
        style={{
          width: "100%",
          position: "relative",
          // height: "100%",
          display: "flex",
          // backgroundImage: "linear-gradient(to right, #f0f2f5, #ffffff)",
          background: "rgb(240,242,245)",
          background:
            "linear-gradient(90deg, rgba(240,242,245,1) 0%, rgba(183,235,234,1) 48%)",
        }}
      >
        <div
          style={{
            width: "25%",
            height: "100%",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
            position: "fixed",
            top: 0,
            overflow: "scroll",
          }}
          className="sider"
        >
          <div
            style={{
              padding: "5px",
              fontSize: "14px",
            }}
          >
            <LeftOutlined />
            <span style={{ marginLeft: "5px" }}>Back to course</span>
          </div>
          <Col
            style={{
              padding: "5px",
            }}
          ></Col>
          <span style={{ marginLeft: "5px", fontWeight: "500" }}>
            Course content
          </span>
          <Collapse
            defaultActiveKey={[0]}
            onChange={handleChange}
            items={CourseContent.map((data, idx) => items(data, idx))}
            className="collapse"
          />
        </div>

        <div
          style={{
            width: "80%",
            height: "100%",
            paddingLeft: "12px",
            marginTop: "20px",
            marginLeft: "25%",
            paddingRight: "12px",
          }}
        >
          <div
            style={{
              width: "100%",
              backgroundColor: "#f7f7f7",
              borderTopLeftRadius: "40px",
              borderTopRightRadius: "30px",
              display: "flex",
              // justifyContent: "space-around",
            }}
          >
            {" "}
            <div
              style={{
                padding: "40px",
              }}
            >
              <Card
                style={{
                  border: "none",
                  backgroundColor: "#f7f7f7",
                }}
                cover={
                  <img
                    alt="example"
                    src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                  />
                }
              >
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <div>
                    <Row>
                      <Avatar
                        src="https://marvel-b1-cdn.bc0a.com/f00000000236551/dt-cdn.net/wp-content/uploads/2021/07/13429_ILL_DevOpsLoop.png"
                        size={45}
                        style={{ marginRight: "8px" }}
                      />
                      <Col>
                        <Col style={{ fontSize: "15px" }}>
                          Devops training certification
                        </Col>
                        <Col style={{ color: "#989a9c" }}>
                          Master DevOps Engineer Course Online
                        </Col>
                      </Col>
                    </Row>
                  </div>
                  <div className="share-hover" style={{ fontSize: "20px" }}>
                    <center>
                      {" "}
                      <ShareAltOutlined />
                    </center>
                  </div>
                </div>
              </Card>{" "}
              <div>
                <h4>
                  {" "}
                  <center>Course details</center>
                </h4>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-around",
                    height: "11%",
                  }}
                >
                  <Card className="smallcard">
                    <Col style={{ fontSize: "12px" }}>Lessons</Col>
                    <Col style={{ fontSize: "12px" }}>
                      <PlayCircleFilled />
                      <span style={{ fontSize: "12px" }}> 10</span>
                    </Col>
                  </Card>
                  <Card className="smallcard">
                    <Col style={{ fontSize: "12px" }}>Duration</Col>
                    <Col style={{ fontSize: "12px" }}>
                      <ClockCircleFilled /> 70hrs 15min
                    </Col>
                  </Card>
                  <Card className="smallcard">
                    <Col style={{ fontSize: "12px" }}>Skill level</Col>
                    <Col>
                      <FundViewOutlined /> Beginner
                    </Col>
                  </Card>
                  {stapData.map((data) => (
                    <StapCard data={data} />
                  ))}
                </div>
                <h4>
                  <center> Course includes</center>
                </h4>
                <div
                  style={{ display: "flex", justifyContent: "space-evenly" }}
                >
                  <div>
                    <Col>
                      <PlayCircleOutlined />{" "}
                      <span style={{ color: "#989a9c" }}>
                        {" "}
                        70hrs video on-demand
                      </span>
                    </Col>
                    <Col>
                      10 <span style={{ color: "#989a9c" }}> Lessons </span>
                    </Col>
                    <Col>
                      58 <span style={{ color: "#989a9c" }}>Topics</span>
                    </Col>
                  </div>
                  <div>
                    <Col>
                      <span style={{ color: "#989a9c" }}>
                        Certificate of completion
                      </span>
                    </Col>
                    <Col>
                      <span style={{ color: "#989a9c" }}>Lifetime access</span>
                    </Col>
                  </div>
                </div>
                <div>
                  <h4>Requirements</h4>
                  <ul style={{ color: "GrayText" }}>
                    <li>Basic Computer Knowledge</li>
                  </ul>
                </div>
                <div>
                  <h4>Description</h4>
                  <span style={{ color: "GrayText" }}>
                    {" "}
                    {"  "} This course is for anybody who wants to get started
                    with DevOps: As there are many tools & technologies in
                    DevOps, it has become difficult for people to choose the
                    right technologies. This course starts from very basics of
                    command line, hands on demonstrations of many tools &
                    technologies. Also most importantly it will show you how
                    various technologies in DevOps work together by setting up
                    your own projects.{" "}
                  </span>
                </div>
                <div>
                  <h4>What you'll learn</h4>
                  <div
                    style={{
                      display: "flex",
                      width: "100%",
                      flexWrap: "wrap",
                      height: "200px",
                    }}
                  >
                    {termsData.map((row) => (
                      <div style={{ width: "220px" }}>
                        <Row
                          style={{
                            display: "flex",
                            justifyContent: "start",
                            alignItems: "start",
                            width: "220px",
                            fontSize: "12px",
                          }}
                        >
                          <Row style={{ width: "20px", marginTop: "3px" }}>
                            <CheckCircleOutlined style={{ color: "green" }} />
                          </Row>
                          <Row style={{ width: "200px" }}>
                            <span style={{ color: "GrayText" }}>{row}</span>
                          </Row>
                        </Row>
                      </div>
                    ))}
                  </div>{" "}
                </div>
              </div>
            </div>
            <div
              style={{
                width: "140%",
                border: "none",
                padding: "28px 15px 0px 0px",
                display: "flex",
                flexDirection: "column",
              }}
            >
              <Card style={{ borderRadius: "10px" }} hoverable>
                <Col>
                  Full course
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "center",
                      marginBottom: "15px",
                    }}
                  >
                    <div>
                      {" "}
                      <span
                        style={{
                          textDecoration: "line-through",
                          marginRight: "4px",
                        }}
                      >
                        $20
                      </span>
                      <span>$16</span>
                    </div>
                    <div
                      style={{
                        backgroundColor: "#c4bafc",
                        color: "#7660fc",
                        fontWeight: "bold",
                        borderRadius: "10px",
                        padding: "3px",
                      }}
                    >
                      {" "}
                      20% discount
                    </div>{" "}
                  </div>
                  <Col style={{ fontWeight: "bold" }}> Course includes:</Col>
                  <Col>
                    <PlayCircleOutlined />{" "}
                    <span style={{ color: "#989a9c" }}>
                      {" "}
                      70hrs video on-demand
                    </span>
                  </Col>
                  <Col>
                    10 <span style={{ color: "#989a9c" }}> Lessons </span>
                  </Col>
                  <Col>
                    58 <span style={{ color: "#989a9c" }}>Topics</span>
                  </Col>
                  <Col>
                    <span style={{ color: "#989a9c" }}>
                      Certificate of completion
                    </span>
                  </Col>
                  <Col>
                    <span style={{ color: "#989a9c" }}>Lifetime access</span>
                  </Col>
                  <center>
                    <Button
                      style={{
                        backgroundColor: "#4e34ed",
                        color: "white",
                        marginTop: "18px",
                        width: "90%",
                        height: "35px",
                      }}
                    >
                      {" "}
                      Add to cart
                    </Button>
                  </center>
                  <center>
                    <Button
                      style={{
                        color: "#4e34ed",
                        backgroundColor: "white",
                        marginTop: "10px",
                        width: "90%",
                        height: "35px",
                        borderColor: "#4e34ed",
                      }}
                      onClick={() => setOpen(true)}
                    >
                      {" "}
                      Buy now
                    </Button>
                  </center>
                </Col>
              </Card>
              <Drawer
                placement={"right"}
                closable={false}
                onClose={() => setOpen(false)}
                open={open}
                style={{
                  position: "absolute",
                  height: "100%",
                  borderRadius: "25px",
                }}
              >
                <RegisterForm />
              </Drawer>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default ProductView;
