import React, { useState } from "react";
import { Avatar, Button, Card, Col, Menu, Row, Collapse, Drawer } from "antd";
import {
  CheckCircleOutlined,
  ShareAltOutlined,
  PlayCircleOutlined,
  PlayCircleFilled,
  FundViewOutlined,
  EyeOutlined,
  ClockCircleFilled,
  LeftOutlined,
  IeOutlined,
  SubnodeOutlined,
} from "@ant-design/icons";
import "./ProductView.css";
import { CourseContent, termsData } from "./const";
import RegisterForm from "../../Utils/Register/Register";
import { StapCard } from "../../Utils/SideBar/StapCard";
import { CertificationSvg } from "../../Assets/CretificationSvg";
import { DownArrowSvg } from "../../Assets/DownArrowSvg";

const ProductView = () => {
  const [open, setOpen] = useState(false);

  const stapData = [
    {
      title: "Certificate of completion",
      type: "certification",
    },
    {
      title: "Course duration 70hrs 15min",
      type: "duration",
    },
    {
      title: "Lessions 10",
      type: "lessions",
    },
    {
      title: "Level up your skills",
      type: "levelup",
    },
  ];

  const spaceBetween = (text, text2) => {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        <div>
          <Col>
            <div style={{ color: "#4e34ed" }}>{text}</div>
            <div style={{ color: "GrayText", fontSize: "12px" }}>3:23 sec</div>
          </Col>
        </div>
        <div>
          <div>
            <PlayCircleOutlined style={{ color: "#8576e3" }} />
            <a className="course-type" style={{ textDecoration: "underline" }}>
              {" "}
              {text2}
            </a>{" "}
          </div>
        </div>
      </div>
    );
  };

  const items = (data, idx) => {
    return {
      key: idx,
      label: (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div className="label"> {data?.title}</div>{" "}
        </div>
      ),
      children: [
        <div>
          <ul className="custom-list">
            {data?.content.map((item) => (
              <li>{spaceBetween(item?.title, "play")}</li>
            ))}
          </ul>
        </div>,
      ],
    };
  };

  const handleChange = () => {};
  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        position: "fixed",
        display: "flex",
        background:
          "linear-gradient(357deg, rgba(0, 0, 0, 1) 0%, rgba(149, 151, 255, 1) 56%)",
      }}
    >
      <div
        style={{
          width: "23%",
          height: "100%",
          // position: "fixed",
          // overflow: "none",
        }}
        className="course-navigation"
      >
        <span
          className="title"
          style={{ paddingInline: "10px", fontSize: "30px" }}
        >
          Course Content
        </span>
        <Collapse
          expandIcon={({ isActive }) => <DownArrowSvg />}
          expandIconPosition="end"
          defaultActiveKey={[0]}
          onChange={handleChange}
          items={CourseContent.map((data, idx) => items(data, idx))}
          className="collapse"
        />
      </div>

      <div
        style={{
          borderTopLeftRadius: "17px",
          borderTopRightRadius: "17px",
          width: "78%",
          height: "100%",
          marginTop: "20px",
          boxShadow: "grey 0px 1px 2px 0px, grey 0px 2px 6px 2px",
          overflowY: "auto",
        }}
      >
        <div
          style={{
            backgroundColor: "white",
            display: "flex",
          }}
        >
          <div
            style={{
              padding: "10px",
            }}
          >
            <Card
              title={
                <div
                  style={{
                    justifyContent: "space-between",
                  }}
                >
                  <div>
                    <Row>
                      <Avatar
                        src="https://marvel-b1-cdn.bc0a.com/f00000000236551/dt-cdn.net/wp-content/uploads/2021/07/13429_ILL_DevOpsLoop.png"
                        size={100}
                        style={{
                          marginRight: "8px",
                          // border: "1px solid",
                        }}
                      />
                      <Col>
                        <Col style={{ fontSize: "15px" }}>
                          Devops training certification
                        </Col>
                        <Col style={{ color: "#989a9c" }}>
                          Master DevOps Engineer Course Online
                        </Col>
                      </Col>
                    </Row>
                  </div>
                </div>
              }
              style={{
                boxShadow:
                  "rgba(17, 17, 26, 0.1) 0px 8px 24px, rgba(17, 17, 26, 0.1) 0px 16px 56px, rgba(17, 17, 26, 0.1) 0px 24px 80px",
                height: "20%",
                // backgroundColor: "#f7f7f7",
                display: "flex",
              }}
            >
              <img
                alt="example"
                src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
              />
            </Card>
            <div>
              <div style={{ fontSize: "18px" }}>
                Course Includes
              </div>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-around",
                  height: "11%",
                }}
              >
                {stapData.map((data) => (
                  <StapCard data={data} />
                ))}
              </div>
              <div style={{ marginTop: "10%" }}>
                <div style={{ fontSize: "18px" }}>Requirements</div>
                <div style={{ fontSize: "14px", color: "GrayText" }}>
                  Basic Computer Knowledge
                </div>
              </div>
              <div>
                <div style={{ fontSize: "18px" }}>Description</div>
                <div style={{ fontSize: "14px", color: "GrayText" }}>
                  This course is for anybody who wants to get started with
                  DevOps: As there are many tools & technologies in DevOps, it
                  has become difficult for people to choose the right
                  technologies. This course starts from very basics of command
                  line, hands on demonstrations of many tools & technologies.
                  Also most importantly it will show you how various
                  technologies in DevOps work together by setting up your own
                  projects.
                </div>
              </div>
              <div>
                <div style={{ fontSize: "18px" }}>What You'll Learn</div>
                <div
                  style={{
                    display: "flex",
                    width: "100%",
                    flexWrap: "wrap",
                    height: "200px",
                  }}
                >
                  {termsData.map((row) => (
                    <div style={{ width: "220px" }}>
                      <Row
                        style={{
                          display: "flex",
                          justifyContent: "start",
                          alignItems: "start",
                          width: "220px",
                          fontSize: "12px",
                        }}
                      >
                        <Row style={{ width: "20px", marginTop: "3px" }}>
                          <CheckCircleOutlined style={{ color: "green" }} />
                        </Row>
                        <Row style={{ width: "200px" }}>
                          <span style={{ color: "GrayText" }}>{row}</span>
                        </Row>
                      </Row>
                    </div>
                  ))}
                </div>{" "}
              </div>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};
export default ProductView;
