import {
  LeftOutlined,
  RightOutlined
} from "@ant-design/icons";
import { Button, Card, Col, Row } from "antd";
import { React, useRef } from "react";
import "typeface-poppins";
import "../../../App.css";
import { CardSlider } from "../../Utils/CardSlider/CardSlider";
import { courses, testimonials } from "./Const";
import './LandingPage.css';
import { TextWithHrLine } from "../../Utils/TextWithHrLine/TextWithHrLine";

export const LandingPage = () => {

  const sliderRef = useRef(null);

  const scrollLeft = () => {
    sliderRef.current.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = () => {
    sliderRef.current.scrollBy({ left: 300, behavior: "smooth" });
  };

  return (
    <main>
      <div>
        <section id="home" className="hero">
          <div className="hero-text">
            <h1>Welcome to Our E-Learning Platform</h1>
            <p>
              Expand your knowledge and skills with our expert-led courses.
              Learn at your own pace and achieve your career goals.
            </p>
          </div>
        </section>

        <section id="courses" className="courses">
          <h2><TextWithHrLine text = {'Our Courses'}/></h2>
          <Row gutter={16}>
            <Button className="arrow-button left" onClick={scrollLeft}>
              <LeftOutlined />
            </Button>
            <div className="card-slider-container" ref={sliderRef}>
              {courses.map((course, index) => (
                <Col span={6} key={index}>
                  <CardSlider course={course} />
                </Col>
              ))}
            </div>
            <Button className="arrow-button right" onClick={scrollRight}>
              <RightOutlined />
            </Button>
          </Row>
        </section>

        <section id="testimonials" className="testimonials">
          <h2><TextWithHrLine text = {'What Our Students Say'}/></h2>
          <Row gutter={16}>
            {testimonials.map((testimonial, index) => (
              <Col span={8} key={index}>
                <Card bordered={false}>
                  <p>
                    <strong>{testimonial.name}</strong>
                  </p>
                  <p>{testimonial.text}</p>
                </Card>
              </Col>
            ))}
          </Row>
        </section>

        <footer id="about" className="footer">
          <h2><TextWithHrLine text = {'About Us'}/></h2>
          <p>
            Our mission is to provide high-quality education accessible to
            everyone. We offer a variety of courses tailored to meet your
            learning needs.
          </p>
        </footer>
      </div>
    </main>
  );
};
