import {
  BellOutlined,
  CalendarOutlined,
  OrderedListOutlined,
  PieChartOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { Card, Col, Drawer } from "antd";
import "./StapCard.css";
import { CertificationSvg } from "../../Assets/CretificationSvg";

export const StapCard = (props) => {
  const title = props?.data?.title;
  const type = props?.data?.type;

  const getSvg = (typ) => {
    switch (typ) {
      case "certification":
        return <CertificationSvg />;
      case "duration":
        return <VideoPlaySvg/>;
      case "error":
        return <div></div>;
      default:
        return <div>Unknown status.</div>; // Fallback for unknown types
    }
  };
  return (
    <center>
      <Card
        className="stap-card"
        style={{
          position: "relative",
          width: "130px",
          height: "130px",
          border: "10px  dotted white",
          background: "rgb(134, 154, 223)",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            height: "70px",
            width: "100px",
            background: "white",
            borderRadius: "8px",
          }}
        >
          <span
            style={{ fontSize: "12px", color: "GrayText", fontWeight: "500" }}
          >
            {title}
          </span>
        </div>
        <div className="stap-card-content">{getSvg(type)}</div>
      </Card>
    </center>
  );
};
